mapper.update:
update team            
set ansnum=ansnum+1   
where grpno=#{grpno} and ansnum>#{ansnum}

mapper.select:
select no, name, gender, skills, phone,grpno, indent, ansnum, r from( select no, name, gender, skills, phone, grpno, indent, ansnum, rownum as r from( select no, name, gender, skills,phone, grpno, indent, ansnum from team
name like '%'||#{word}||'%'
phone like '%'||#{word}||'%'
skills like '%'||#{word}||'%'
order by grpno desc, ansnum )
) where r>=#{sno} and r<=#{eno}
